/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect } from 'react'
import { Container } from "react-bootstrap"
import { useParams } from "react-router-dom"
import ProfileHeader from "../components/profile/header/ProfileHeader"
import { useFetch } from "../hooks/useFetch"
import { ApiService } from '../API/ApiService'
import { FromApiUser, PostType } from '../types/Interfaces'

const UserProfile = () => {
  const param = useParams<{id: string}>()
  const [gotUser, setGotUser] = useState<FromApiUser>()
  const [fetching, loading] = useFetch(async () => {
    const userFromAPI = await ApiService.getOneUser(Number(param.id))
    setGotUser(userFromAPI);
  })

  useEffect(() => {
    fetching()
  }, [])

  return (
    <Container>
        <ProfileHeader userLogin={gotUser!.username} userEmail={gotUser!.email} loading={loading}/>
    </Container>
  )
}

export default UserProfile
