import { useState } from 'react';
import Alert from 'react-bootstrap/Alert';

interface AlertModalProps {
  text: string,
  visible: boolean,
  className?: string,
}

const AlertModal = ({text, visible, className}: AlertModalProps) => {
  const [show, setShow] = useState(visible);

  if (show) {
    console.log(true);
    
    return (
      <Alert variant="danger" onClose={() => setShow(false)}  className={className} dismissible>
        <p>
          {text}
        </p>
      </Alert>
    );
  }

  return <></>
}

export default AlertModal
