/* eslint-disable react-hooks/exhaustive-deps */
import { useMemo } from 'react'
import { PostType } from "../types/Interfaces"
import Post from '../components/Post/Post';

export const useSearchPost = (Posts: PostType[], searchingItem: string, cache?: boolean) => {
    console.log(Post);
    let searchedPost
    searchedPost = useMemo(() => {
        let cacheDates;
        
        if (cache !== undefined || !!cache) {
            cacheDates = [...Posts]
        }

        const filteredPosts = Posts.filter(post => post.postTitle.toLocaleLowerCase().includes(searchingItem.toLocaleLowerCase()))

        return filteredPosts.length <= 0 ? cacheDates : filteredPosts
    }, [Posts, searchingItem])

    return searchedPost
}
