/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect } from 'react'
import { Container } from "react-bootstrap"
import { useParams } from "react-router-dom"
import ProfileHeader from "../components/profile/header/ProfileHeader"
import { ApiService } from '../API/ApiService'
import { useFetch } from '../hooks/useFetch'
import { FromApiUser } from '../types/Interfaces'

const UserProfile = () => {
  const param = useParams<{id: string}>()
  const [user, setUser] = useState<Omit<FromApiUser, "id">>({
    username: '',
    email: '',
  })

  const [fetching, loading, errorMessage] = useFetch(async () => {
    const response = await ApiService.getUserById(Number(param.id))
    setUser({
      username: response.username,
      email: response.email
    })
  })

  useEffect(() => {
    fetching()
  }, [])

  return (
    <div className='mt-5'>
    <Container>
        <ProfileHeader loading={loading} userLogin={user.username} userEmail={user.email}/>
    </Container>
    </div>
  )
}

export default UserProfile
