/* eslint-disable react-hooks/rules-of-hooks */
import Button from "react-bootstrap/Button"
import { useContext } from 'react'
import Global from "../../../context/Global"

const DeleteAllPostsButton = () => {
    const {localStore, setLocalStore} = useContext(Global)

    return (
    <Button onClick={() => setLocalStore({...localStore, myPosts: []})} variant="outline-danger">Delete all posts</Button>
  )
}

export default DeleteAllPostsButton
