import Modal from "react-bootstrap/Modal"
import Button from 'react-bootstrap/Button'
import { useState } from "react"

interface AlertSureProps {
    ifIsYes: () => void,
    show: boolean
}

const AlertSure = ({ifIsYes, show}: AlertSureProps) => {
    const [visible, setVisible] = useState(show) 

    const eventModal = (typeEvent: 'yes' | 'no') => {
        if (typeEvent === 'yes') { ifIsYes() }
        else { setVisible(false) }
    }

    return (
    <div
      className="modal"
      style={{ display: visible ? 'block' : 'none' }}
    >
      <Modal.Dialog>
        <Modal.Body>
          <p>Are you sure ?</p>
        </Modal.Body>

        <Modal.Footer>
          <Button onClick={() => eventModal('yes')} variant="success">yes</Button>
          <Button onClick={() => eventModal('no')} variant="danger">no</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  )
}

export default AlertSure
