import { useState } from 'react'
import Alert from 'react-bootstrap/Alert';

interface AlertModalProps {
  text: string,
  className?: string,
  visible: boolean
}

const AlertModal = ({text, visible, className}: AlertModalProps) => {
  const [visibleState, setVisibleState] = useState(visible)

  console.log(visible);

  return (
    <Alert variant="danger" className={className} dismissible onClick={() => setVisibleState(false)} style={{display: visibleState ? 'block' : 'none'}}>
      <p>
        {text}
      </p>
    </Alert>
  );
}

export default AlertModal
