/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect, useMemo } from "react";
import Filter from "../components/Filter/Filter";
import List from "../components/List/List";
import Post from "../components/Post/Post";
import { useFetch } from "../hooks/useFetch";
import { ApiService } from "../API/ApiService";
import { PostType } from "../types/Interfaces";
import { Container } from "react-bootstrap";

const WorldPosts = () => {
  const [filteredPosts, setFilterdPosts] = useState<PostType[]>([]);
  const staticPosts = useMemo(() => [...filteredPosts], [filteredPosts])
  const [fecthAPI, loading, errorMessage] = useFetch(async () => { 
    const postsFromAPI = await ApiService.getPosts({
      limit: 100,
      page: 1
    })
    setFilterdPosts(postsFromAPI)  
  })
  
  useEffect(() => {
    fecthAPI()
  }, [])
  
  
  

  return (
    <Container>
      <h1>Filter: </h1>
      {loading && <h1>Loading...</h1>}
      <Filter Posts={filteredPosts} setFilteredPosts={setFilterdPosts} />
      <List items={filteredPosts} renderItem={(item) => {
        return <Post
          isAuthor={false}
          postId={item.postId}
          postTitle={item.postTitle}
          postBody={item.postBody}
          createdDate={item.createdDate}
          userId={item.userId}
          authorLogin={item.authorLogin}
          key={item.postId}
          className="post"
        />
      }} />
    </Container>
  );
};

export default WorldPosts;
