import { createContext, Dispatch, SetStateAction } from "react";
import { ContextType } from "../types/Interfaces";

type setStateFuntion = Dispatch<SetStateAction<any>> 

const defaultState: ContextType<setStateFuntion> = {
    setLocalStore: () => undefined,    
    localStore: {
        defualtAvatar: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRLGOQ8_ZWa44gudip73QsgXG0iMzTGg1t9BUspTHE0Qw&s',
        User: {
            userLogin: 'Login',
            userEmail: '',
            userPassword: '',
        },
        
        myPosts: [],
    },
} 

const Global = createContext<ContextType<setStateFuntion>>(defaultState)

export default Global
