import { useMemo } from 'react'
import { PostType } from "../types/Interfaces"

export const useSearchPost = (Posts: PostType[], searchingItem: string) => {
    let searchedPost
    searchedPost = useMemo(() => {
        console.log(Posts);
        return Posts.filter(post => post.postTitle.toLocaleLowerCase() === searchingItem.toLocaleLowerCase())
    }, [Posts, searchingItem])

    return searchedPost
}
