import Modal from "react-bootstrap/Modal"
import Button from 'react-bootstrap/Button'
import { useState } from "react"

interface AlertSureProps {
    ifIsYes: () => void,
    ifIsNo: () => void,
}

const AlertSure = ({ifIsYes, ifIsNo}: AlertSureProps) => {

    const eventModal = (typeEvent: 'yes' | 'no') => {
        if (typeEvent === 'yes') { ifIsYes() }
        else { ifIsNo() }
    }

    return (
    <div
      className="modal show"
      style={{ display: 'block'}}
    >
      <Modal.Dialog>
        <Modal.Body>
          <p>Are you sure ?</p>
        </Modal.Body>

        <Modal.Footer>
          <Button onClick={() => eventModal('yes')} variant="success">yes</Button>
          <Button onClick={() => eventModal('no')} variant="danger">no</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  )
}

export default AlertSure
