import Window from "../components/window/Window";
import { UserDataType } from "../types/Interfaces";
import { useState, useContext } from "react";
import Global from "../context/Global";
import { Form } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import AlertModal from "../components/alert/AlertModal";

interface stateAlertType {
  visibleAlert: boolean,
  textAlert: string,
}

const LogInPage = () => {
  const { setLocalStore, localStore } = useContext(Global);
  const [stateAlert, setStateAlert] = useState<stateAlertType>({
    visibleAlert: false,
    textAlert: '',
  })

  const [loginData, setLoginData] = useState<UserDataType>({
    userLogin: "",
    userEmail: "",
    userPassword: "",
  });

  const sendUserDataInLS = () => {
    if (
      !!loginData.userEmail &&
      !!loginData.userLogin &&
      !!loginData.userPassword
    ) {
      setLocalStore({ ...localStore, user: loginData });
    }

    else {      
      setStateAlert({
        visibleAlert: true,
        textAlert: 'Inputs value must be something',
      })
    }
  };

  return (
    <Window>
      <AlertModal visible={stateAlert.visibleAlert} className="col-12" text={stateAlert.textAlert}/>
      <h4 className="text-center">Log in</h4>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Login</Form.Label>
        <Form.Control
          type="text"
          placeholder="Login"
          onChange={(event) =>
            setLoginData({ ...loginData, userLogin: event.target.value })
          }
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          placeholder="Enter email"
          onChange={(event) =>
            setLoginData({ ...loginData, userEmail: event.target.value })
          }
        />
        <Form.Text className="text-muted">
          We'll never share your email with anyone else.
        </Form.Text>
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control
          type="password"
          placeholder="Password"
          onChange={(event) =>
            setLoginData({ ...loginData, userPassword: event.target.value })
          }
        />
      </Form.Group>

      <Button
        variant="primary"
        type="submit"
        className="col-12"
        onClick={sendUserDataInLS}
      >
        Log in
      </Button>
    </Window>
  );
};

export default LogInPage;
