/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect } from 'react'
import { Container } from "react-bootstrap"
import { useParams } from "react-router-dom"
import ProfileHeader from "../components/profile/header/ProfileHeader"
import { useFetch } from "../hooks/useFetch"
import { ApiService } from '../API/ApiService'
import { FromApiUser } from '../types/Interfaces'

type user = Omit<FromApiUser, "id">

interface userType extends user {
  avatar: string
}

const UserProfile = () => {
  const param = useParams<{id: string}>()
  const [gotUser, setGotUser] = useState<userType>();

  const [fecthAPI, loading, errorMessage] = useFetch(async () => { 
    const userFromAPI = await ApiService.getOneUser(Number(param.id))
    
    setGotUser({
      username: userFromAPI.username,
      email: userFromAPI.email,
      avatar: userFromAPI.image
    })
  })
  
  useEffect(() => {
    fecthAPI()
    console.log(gotUser);
  }, [])

  return (
    <Container>
        <ProfileHeader userLogin={gotUser!.username} userEmail={gotUser!.email} loading={loading}/>
    </Container>
  )
}

export default UserProfile
