import { useContext, useState, ChangeEvent, useEffect } from 'react'
import Global from "../../context/Global"
import { PostType } from "../../types/Interfaces"
import SearchInput from "../Inputs/SearchInput/SearchInput"
import Select from "../select/Select"
import { SelectDateItems } from './FilterData'
import { useSearchPost } from '../../hooks/useSearchPost'

const Filter = ({filterPosts}: {filterPosts: PostType[]}) => {
  const {localStore, setLocalStore} = useContext(Global)
  const [searchingItem, setSearchingItem] = useState<string>('')
  
  const searchedPosts = useSearchPost(localStore.myPosts, searchingItem, true)

  useEffect(() => {
    // console.log(searchedPosts);
    setLocalStore({...localStore, myPosts: searchedPosts})
    
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchingItem])

  return (
    <>
        <SearchInput value={searchingItem} onChange={(event: ChangeEvent<HTMLInputElement>) => setSearchingItem(event.target.value)}/>
        <Select defualtOption="Sort on..." options={SelectDateItems}/>
    </>
  )
}

export default Filter
