import Alert from 'react-bootstrap/Alert';

interface AlertModalProps {
  text: string,
  className?: string,
}

const AlertModal = ({text, className}: AlertModalProps) => {
  return (
    <Alert variant="danger" onClose={() => setShow(false)}  className={className} dismissible>
      <p>
        {text}
      </p>
    </Alert>
  );
}

export default AlertModal
