/* eslint-disable react-hooks/exhaustive-deps */
import { useContext, useState, useEffect } from "react";
import Global from "../context/Global";
import Filter from "../components/Filter/Filter";
import Post from "../components/Post/Post";
import { useFetch } from "../hooks/useFetch";
import { ApiService } from "../API/ApiService";
import { ApiPostType, PostType } from "../types/Interfaces";
import { createRandomDate } from '../utils/utilsFn'

const WorldPosts = () => {
  const { localStore } = useContext(Global);
  // const [filteredPosts, setFilterdPosts] = useState([]);
  const [fecthAPI, loading, errorMessage] = useFetch(async () => {
    
    
    const otherUsersPosts = (await ApiService.getPosts()).data.map((post: ApiPostType) => {
      const apiPost: PostType = {
        postId: post.id,
        postTitle: post.title,
        postBody: post.body,
        createdDate: createRandomDate(),
        authorLogin: 'Login',
        userId: 1
      }

      return apiPost
    }) 
  })

  useEffect(() => {
    fecthAPI()
  }, [])
  

  return (
    <>
      <h1>Filter: </h1>
      {loading && <h1>Loading...</h1>}
      {/* <Filter Posts={localStore.myPosts} setFilteredPosts={setFilterdPosts} /> */}
    </>
  );
};

export default WorldPosts;
