/* eslint-disable react-hooks/rules-of-hooks */
import Button from "react-bootstrap/Button"
import { useContext } from 'react'
import Global from "../../../context/Global"
import { TypeSetState } from "../../../types/types"

interface DeleteAllPostsButtonProps {
    setVisibleAlert: TypeSetState<boolean>,
    yesSure: boolean
}

const DeleteAllPostsButton = ({setVisibleAlert, yesSure}: DeleteAllPostsButtonProps) => {
    const {localStore, setLocalStore} = useContext(Global)

    const deleteAllPostsFn = () => {
        setVisibleAlert(true)
        console.log(true);
    
        if (yesSure) {
            setLocalStore({...localStore, myPosts: []})
        }
    }

    return (
    <Button onClick={deleteAllPostsFn} variant="outline-danger">Delete all posts</Button>
  )
}

export default DeleteAllPostsButton
