import { ReactNode, useContext } from 'react'
import Global from '../../../context/Global'
import Button from "react-bootstrap/Button"

interface DeletePostButtonProps {
    children: ReactNode,
    postId?: number,
}

const DeletePostButton = ({children, postId}: DeletePostButtonProps) => {
    const {localStore, setLocalStore} = useContext(Global)

    const deletePostFn = () => setLocalStore({...localStore, myPosts: [localStore.myPosts.filter(post => post.postId !== postId)  ]})  

    return (
        <Button variant="danger" title='Delete this post?'>{children}</Button>
    )
}

export default DeletePostButton
