import { useMemo } from 'react';
import { PostType, OptionSelectType } from './../types/Interfaces';
import { sortEnum } from '../types/enums';

interface useSearchPostProps {
    posts: PostType[],
    sortOption: Omit<OptionSelectType, "id">,
}

const useSortPosts = ({posts, sortOption}: useSearchPostProps) => {
    let sortedPosts
    sortedPosts = useMemo(() => {
        if (sortOption.value === sortEnum.Date) {
            return posts.sort((post, post2) => post.createdDate.localeCompare(post2.createdDate))
        }
        else if (sortOption.value === sortEnum.Title) {
            return posts.sort((post, post2) => post.postTitle.localeCompare(post2.postTitle))
        }
        else if (sortOption.value === sortEnum.Description) {
            return posts.sort((post, post2) => post.postBody.localeCompare(post2.postBody))
        }
        else {
            return posts
        }
    }, [posts, sortOption])

    return sortedPosts
}

export default useSortPosts
