import { useContext } from "react"
import Global from "../../context/Global"
import { PostType } from "../../types/Interfaces"
import { Card } from "react-bootstrap"

const Post = ({postId, postTitle, postBody, authorLogin}: PostType) => {  
    const {localStore} = useContext(Global)
    
    return (
        <Card>
            <Card.Header>
                <img src={localStore.defualtAvatar} alt="Avatar" style={{width: '30px', height: '30px', borderRadius: '50%'}}/>
            </Card.Header>
        </Card>
    )
}

export default Post
