import { useState } from 'react'
import Alert from 'react-bootstrap/Alert';

interface AlertModalProps {
  text: string,
  className?: string,
  visible: boolean
}

const AlertModal = ({text, visible, className}: AlertModalProps) => {
  const [visibleState, setVisibleState] = useState<boolean>(true)

  if (visibleState === true && visible === true) {
    return (
      <Alert variant="danger" className={className} dismissible onClick={() => setVisibleState(false)}>
        <p className='m-0'>
          {text}
        </p>
      </Alert>
    );
  }

  return <></>
}

export default AlertModal
