import { ReactNode, useContext, useEffect } from 'react'
import Global from '../../../context/Global'
import Button from "react-bootstrap/Button"

interface DeletePostButtonProps {
    children: ReactNode,
    postId: number,
}

const DeletePostButton = ({children, postId}: DeletePostButtonProps) => {
    const {localStore, setLocalStore} = useContext(Global)

    const deletePostFn = () => { return localStore.myPosts.filter(post => post.postId !== postId) }
    useEffect(() => {
        deletePostFn()
    }, [])

    return (
        <Button onClick={deletePostFn} variant="danger" title='Delete this post?'>{children}</Button>
    )
}

export default DeletePostButton
