import Card from "react-bootstrap/Card";
import { UserDataType } from "../../../types/Interfaces";
import classes from "./ProfileHeader.module.css";
import { useContext } from "react";
import Global from "../../../context/Global";
import { isAvatar } from "../../../types/guards ";
import List from "../../List/List";
import Post from "../../Post/Post";

type ProfileProps = Omit<UserDataType, "userPassword" | "userPassword" | "isAuth" | "isInSystem">;
interface ProfileExtendsProps extends ProfileProps {
  avatar?: string;
}

const ProfileHeader = ({userLogin, userEmail, avatar,}: ProfileExtendsProps) => {
  const { localStore } = useContext(Global);

  return (
    <>
      <Card>
        <Card.Header>
          <div className={classes.header}>
            <img
              src={isAvatar(avatar) ? avatar : localStore.defualtAvatar}
              alt="avatar"
              className={classes.avatar}
            />
            <ul className={classes.userInfoList}>
              <li>{userLogin}</li>
              <li>{userEmail}</li>
            </ul>
          </div>
        </Card.Header>
      </Card>
      <List items={localStore.myPosts} renderItem={post => {
        return <Post 
          postId={post.postId} 
          postTitle={post.postTitle}
          postBody={post.postBody}
          isAuthor={true}
          createdDate={post.createdDate}
          userId={post.userId}
          avatar={post.avatar}
          authorLogin={post.authorLogin}/>
      }}/>
    </>
  );
};

export default ProfileHeader;
