import { createRandomDate } from "./../utils/utilsFn";
import {
  FromApiPostType,
  PostType,
  FromApiUser,
  otherUser,
} from "./../types/Interfaces";
import axios from "axios";

interface ParamsAPI {
  _limit: number;
}

export class ApiService {
  private static URL_USERS = "https://jsonplaceholder.typicode.com/users";
  private static URL_POSTS = "https://jsonplaceholder.typicode.com/posts";

  static async getUsers(): Promise<otherUser[]> {
    const fromAPIUsers = (await axios.get(this.URL_USERS)).data.map(
      (user: FromApiUser): otherUser => {
        return {
          id: user.id,
          login: user.username,
          email: user.email,
        };
      }
    );

    return fromAPIUsers;
  }

  static async getPosts(): Promise<PostType[]> {
    const usersDatas = await this.getUsers();
    const usersPosts = await axios.get(this.URL_POSTS)
    let resultPosts: PostType[] = []

    usersPosts.data.forEach((post: FromApiPostType, index: number) => {      
      // if (resultPosts.length !== 10) {
        
      // }
      resultPosts.push({
        postId: post.id,
        postTitle: post.title,
        postBody: post.body,
        createdDate: createRandomDate(),
        authorLogin: usersDatas[index].login,
        userId: usersDatas[index].id
      })
    })

    return resultPosts !== undefined ? resultPosts : [];
  }
}
