/* eslint-disable react-hooks/exhaustive-deps */
import { useState, useEffect, useRef, MutableRefObject } from "react";
import Filter from "../components/Filter/Filter";
import List from "../components/List/List";
import Post from "../components/Post/Post";
import { useFetch } from "../hooks/useFetch";
import { ApiService } from "../API/ApiService";
import { PostType } from "../types/Interfaces";
import { Container } from "react-bootstrap";
import Loader from "../components/loader/Loader";

// ToDo: create lead posts with observer

const WorldPosts = () => {
  const [filteredPosts, setFilterdPosts] = useState<PostType[]>([]);
  const [staticPosts, setStaticPosts] = useState<PostType[]>([])
  let [pagePosts, setPagePosts] = useState<number>(0)
  let [limitPosts, setLimitPosts] = useState<number>(10)
  const lastElement = useRef() as MutableRefObject<HTMLDivElement> 
  const [fecthAPI, loading, errorMessage] = useFetch(async () => { 
    const postsFromAPI = await ApiService.getPosts({
      limit: 10,
      page: pagePosts,
    })
    setFilterdPosts(postsFromAPI)
    setStaticPosts(postsFromAPI)
  })
  
  useEffect(() => {
    const observer = new IntersectionObserver((observer) => {
      if (observer[0].isIntersecting && pagePosts < 10 && limitPosts < 100) {
        setPagePosts(pagePosts += 1)
        setLimitPosts(limitPosts += 10)
      }
    })

    observer.observe(lastElement.current)
  
  }, [])

  useEffect(() => {
    fecthAPI()
  }, [pagePosts, limitPosts])
    

  return (
    <Container>
      <h1 className="mt-5">Filter: </h1>
      <br />
      <Filter Posts={staticPosts} setFilteredPosts={setFilterdPosts} />
      <hr />
      <br />
      {loading && <Loader />}
      {!!errorMessage && <h1 className="text-danger text-center">Error load posts: {errorMessage}</h1>}

      <List items={filteredPosts} renderItem={(item) => {
        return <Post
          isAuthor={false}
          postId={item.postId}
          postTitle={item.postTitle}
          postBody={item.postBody}
          createdDate={item.createdDate}
          userId={item.userId}
          authorLogin={item.authorLogin}
          key={item.postId}
          className="post"
        />
      }} />
      <div ref={lastElement} style={{height: 30}}/>

    </Container>
  );
};

export default WorldPosts;
