import { createRandomDate } from './../utils/utilsFn';
import { FromApiPostType, PostType, FromApiUser, otherUser } from './../types/Interfaces';
import axios from "axios";

export class ApiService {
  private static URL_USERS = "https://jsonplaceholder.typicode.com/users"
  private static URL_POSTS = "https://jsonplaceholder.typicode.com/posts";


  static async getUsers(): Promise<otherUser[]> {
    const fromAPIUsers = (await axios.get(this.URL_USERS)).data.map((user: FromApiUser): otherUser => {      
      return {
        id: user.id,
        login: user.username,
        email: user.email
      }
    })

    return fromAPIUsers
  }

  static async getPosts(): Promise<PostType[]> {
    const usersData = await this.getUsers()
    
    const fromAPIPosts = (await axios.get(this.URL_POSTS)).data.map((post: FromApiPostType) => {
      let postFromAPI
      usersData.forEach(user => {
        console.log(user);
        postFromAPI = {
          postId: post.id,
          postTitle: post.title,
          postBody: post.body,
          createdDate: createRandomDate(),
          userId: user.id,
          authorLogin: user.login
        }
      })

      return postFromAPI !== undefined ? postFromAPI : post
    })

    return fromAPIPosts
  }
}
